import React from 'react';
import { getStatistics } from '../actions/statisticActions';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

class Statistics extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            isEmpty: true
        }
    }


    componentDidMount() {
        this.setState({ isEmpty: false });

        console.log("componentDidMount()")

        if(!this.state.isEmpty) {
            const statistics = this.props.getStatistics();
        }
    }

    render() {
        const { statistics } = this.props.statistic;
        let isEmpty = this.state.isEmpty;
        return (
            <div>
                Statistics
                <div className="table-responsive-sm">
                    <table className="table table-striped table-dark table-radius table-hover table-margin">
                        <tbody>
                        {   !isEmpty &&
                            statistics.map(statistic => (
                                <tr>
                                    <td>{statistic.userName}</td>
                                    <td>{statistic.time}</td>
                                    <td>{statistic.date}</td>
                                </tr>
                            ))
                        }
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }
}

Statistics.propTypes = {
    statistic: PropTypes.object.isRequired,
    getStatistics: PropTypes.func.isRequired
}

const mapStateToProps = state => ({
    statistic: state.statistic
})

export default connect(mapStateToProps, { getStatistics })(Statistics);
